import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './AccidentReport.css';

const AccidentReport = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    incidentDate: '',
    incidentTime: '',
    location: '',
    incidentType: 'slip',
    description: '',
    injuries: '',
    witnesses: '',
    files: null
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setFormData(prev => ({
      ...prev,
      files: e.target.files
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    // Basic validation
    if (!formData.incidentDate || !formData.location) {
      setError('Please fill in all required fields');
      setIsSubmitting(false);
      return;
    }

    try {
      const formPayload = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        if (key === 'files' && value) {
          Array.from(value).forEach(file => {
            formPayload.append('files', file);
          });
        } else if (value) {
          formPayload.append(key, value);
        }
      });

      await new Promise(resolve => setTimeout(resolve, 1500));
      console.log('Form submitted:', formData);
      alert('Accident report submitted successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError('Failed to submit report. Please try again.');
      console.error('Submission error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="page-container">
      {/* Header */}
      <header className="app-header">
        <div className="header-content">
          <h1>SafetyFirst</h1>
          <nav className="header-nav">
            <button onClick={() => navigate('/')}>Home</button>
            <button onClick={() => navigate('/dashboard')}>Dashboard</button>
          </nav>
        </div>
      </header>

      {/* Main Content */}
      <main className="accident-report-container">
        <div className="report-header">
          <h2>Incident Report Form</h2>
          <p>Please provide details about the workplace incident</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="report-form">
          <div className="form-section">
            <h3>Incident Details</h3>
            <div className="form-row">
              <div className="form-group">
                <label>Date of Incident *</label>
                <input
                  type="date"
                  name="incidentDate"
                  value={formData.incidentDate}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Time of Incident</label>
                <input
                  type="time"
                  name="incidentTime"
                  value={formData.incidentTime}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Location *</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                required
                placeholder="Building, floor, or specific area"
              />
            </div>

            <div className="form-group">
              <label>Type of Incident *</label>
              <select
                name="incidentType"
                value={formData.incidentType}
                onChange={handleChange}
                required
              >
                <option value="slip">Slip/Trip/Fall</option>
                <option value="strain">Musculoskeletal Strain</option>
                <option value="struck">Struck By/Against Object</option>
                <option value="chemical">Chemical Exposure</option>
                <option value="equipment">Equipment Mishap</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label>Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
                rows="4"
                placeholder="Provide a detailed description of what happened..."
              />
            </div>

            <div className="form-group">
              <label>Injuries Sustained</label>
              <input
                type="text"
                name="injuries"
                value={formData.injuries}
                onChange={handleChange}
                placeholder="Describe any injuries (if applicable)"
              />
            </div>

            <div className="form-group">
              <label>Witnesses (if any)</label>
              <input
                type="text"
                name="witnesses"
                value={formData.witnesses}
                onChange={handleChange}
                placeholder="Names of witnesses"
              />
            </div>
          </div>

          <div className="form-section">
            <h3>Supporting Documents</h3>
            <div className="form-group">
              <label>Upload Photos/Documents</label>
              <input
                type="file"
                name="files"
                onChange={handleFileChange}
                multiple
                accept="image/*,.pdf,.doc,.docx"
              />
              <small>You can upload multiple files (images, PDFs, or Word docs)</small>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="submit"
              className="submit-btn"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Submitting...' : 'Submit Report'}
            </button>
            <button
              type="button"
              className="cancel-btn"
              onClick={() => navigate(-1)}
            >
              Cancel
            </button>
          </div>
        </form>
      </main>

      {/* Footer */}
      <footer className="app-footer">
        <div className="footer-content">
          <p>Â© 2024 SafetyFirst. All rights reserved.</p>
          <div className="footer-links">
            <a href="/privacy">Privacy Policy</a>
            <a href="/terms">Terms of Service</a>
            <a href="/contact">Contact Us</a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default AccidentReport;
