// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/Footer.css';
import '../CSS/Header.css';
import '../CSS/HomePage.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = () => {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const handleGetStarted = () => {
    if (isAuthenticated) {
      navigate('/dashboard');
    } else {
      navigate('/login');
    }
  };

  return (
    <div className="home-page">
      <Header isAuthenticated={isAuthenticated} />

      <section className="hero-outer">
        <div className="hero">
          <div className="container">
            <div className="hero-content">
              <h1>Harassment isn't your shame, it's a crime</h1>
              <p className="subtitle">
                Every Second You Wait, They Get Away With It. Report Now
              </p>
              <button onClick={handleGetStarted} className="btn-prim btn-large">
                {isAuthenticated ? 'Go to Dashboard' : 'Get Started - It\'s Free'}
              </button>
            </div>
            <div className="hero-image">
              <img src="/images/speakup.png" alt="SpeakUpHer illustration" />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default HomePage;
