import React, { useState } from 'react';
import '../CSS/AccidentReport.css';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';

const IncidentReportForm = () => {
  const [formData, setFormData] = useState({
    date: '',
    time: '',
    submissionDate: '',
    location: '',
    incidentType: '',
    description: '',
    witnesses: '',
    files: null
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      files: e.target.files
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
    console.log(formData);
  };

  return (
    <div className="incident-report-container">
      <h1>Incident Report Form</h1>
      
      <form onSubmit={handleSubmit}>
        <div className="form-row">
          <div className="form-group">
            <label>DATE OF INCIDENT</label>
            <input
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>TIME OF INCIDENT</label>
            <input
              type="time"
              name="time"
              value={formData.time}
              onChange={handleInputChange}
              required
            />
          </div>
        </div>

        <div className="form-group">
          <label>DATE OF SUBMISSION</label>
          <input
            type="date"
            name="submissionDate"
            value={formData.submissionDate}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>LOCATION</label>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleInputChange}
            placeholder="Office Building 3rd Floor, Break Room"
            required
          />
        </div>

        <div className="form-group">
          <label>TYPE OF INCIDENT</label>
          <select
            name="incidentType"
            value={formData.incidentType}
            onChange={handleInputChange}
            required
          >
            <option value="">Select</option>
            <option value="harassment">Harassment</option>
            <option value="discrimination">Discrimination</option>
            <option value="safety">Safety Concern</option>
            <option value="other">Other</option>
          </select>
        </div>

        <div className="form-group">
          <label>DESCRIPTION</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label>WITNESSES</label>
          <input
            type="text"
            name="witnesses"
            value={formData.witnesses}
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group">
          <label>UPLOAD IMAGES/DOCUMENTS</label>
          <input
            type="file"
            onChange={handleFileChange}
            multiple
          />
          <div className="file-upload-text">Choose Files</div>
        </div>

        <div className="form-actions">
          <button type="submit" className="submit-button">Submit</button>
          <button type="button" className="edit-button">Edit</button>
          <button type="button" className="delete-button">Delete</button>
        </div>
      </form>

      <div className="products-section">
        <h2>Products</h2>
        <div className="product-links">
          <a href="/report">Report Now!</a>
          <a href="/account">Account</a>
          <a href="/support">Support</a>
          <a href="/privacy">Privacy Policy</a>
        </div>
      </div>
    </div>
  );
};

export default IncidentReportForm;
