import React, { useRef, useState } from 'react';
import '../styles/FileUpload.css';

const FileUpload = ({ onFilesChange, maxFileSize = 5 }) => {
  const [dragActive, setDragActive] = useState(false);
  const [error, setError] = useState('');
  const inputRef = useRef(null);
  
  const ACCEPTED_TYPES = {
    'image/jpeg': ['.jpg', '.jpeg'],
    'image/png': ['.png'],
    'application/pdf': ['.pdf'],
    'application/msword': ['.doc'],
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
    'text/plain': ['.txt']
  };

  const validateFile = (file) => {
    // Check file size (convert maxFileSize from MB to bytes)
    if (file.size > maxFileSize * 1024 * 1024) {
      return `File ${file.name} is too large. Maximum size is ${maxFileSize}MB`;
    }

    // Check file type
    if (!Object.keys(ACCEPTED_TYPES).includes(file.type)) {
      return `File ${file.name} is not an accepted file type`;
    }

    return null;
  };

  const handleFiles = (files) => {
    const fileList = Array.from(files);
    setError('');

    // Validate each file
    const errors = [];
    const validFiles = fileList.filter(file => {
      const error = validateFile(file);
      if (error) {
        errors.push(error);
        return false;
      }
      return true;
    });

    if (errors.length > 0) {
      setError(errors.join('\n'));
    }

    if (validFiles.length > 0) {
      onFilesChange(validFiles);
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      handleFiles(e.dataTransfer.files);
    }
  };

  const handleChange = (e) => {
    e.preventDefault();
    if (e.target.files && e.target.files.length > 0) {
      handleFiles(e.target.files);
    }
  };

  const handleClick = () => {
    inputRef.current.click();
  };

  const acceptedExtensions = Object.values(ACCEPTED_TYPES).flat().join(',');

  return (
    <div className="file-upload">
      <div 
        className={`upload-container ${dragActive ? 'drag-active' : ''}`}
        onDragEnter={handleDrag}
        onDragLeave={handleDrag}
        onDragOver={handleDrag}
        onDrop={handleDrop}
        onClick={handleClick}
      >
        <input
          ref={inputRef}
          type="file"
          multiple
          className="file-input"
          onChange={handleChange}
          accept={acceptedExtensions}
        />
        <div className="upload-message">
          <i className="upload-icon">ðŸ“Ž</i>
          <p>Drag and drop files here or click to select</p>
          <span className="upload-hint">
            Accepted files: PDF, DOC, DOCX, TXT, JPG, JPEG, PNG (Max {maxFileSize}MB)
          </span>
        </div>
      </div>
      {error && (
        <div className="upload-error">
          {error.split('\n').map((err, index) => (
            <p key={index}>{err}</p>
          ))}
        </div>
      )}
    </div>
  );
};

export default FileUpload;
