import React from 'react';
import { Link } from 'react-router-dom';
import './ArticlesPage.css'; // Create this CSS file
import './HomePage.css'; // Reuse HomePage styles

const ArticlesPage = () => {
  const isAuthenticated = localStorage.getItem('isAuthenticated');
  const articles = [
    {
      id: 1,
      title: "Recognizing Different Forms of Harassment",
      summary: "Learn to identify verbal, physical, and digital harassment in workplace and public settings.",
      content: "Harassment can take many forms... [detailed content]",
      category: "awareness"
    },
    {
      id: 2,
      title: "De-escalation Techniques for Confrontational Situations",
      summary: "Practical strategies to safely defuse tense encounters.",
      content: "When faced with aggression... [detailed content]",
      category: "response"
    },
    {
      id: 3,
      title: "Digital Safety: Protecting Your Online Presence",
      summary: "Essential steps to secure your social media and digital communications.",
      content: "Cyber harassment is increasingly common... [detailed content]",
      category: "digital"
    },
    {
      id: 4,
      title: "Legal Rights and Reporting Procedures in Jordan",
      summary: "Understand your legal protections and how to formally report incidents.",
      content: "Jordanian law provides specific protections... [detailed content]",
      category: "legal"
    }
  ];

  return (
    <div className="home-page">
      {/* Navigation Bar - Fixed at top */}
      <header className="navbar-container">
        <nav className="navbar">
          <div className="container">
            <div className="logo nav-links">
              <Link to="/">
                <img src="/images/SpeakUp-Her.png" alt="SpeakUpHer Logo" className="logo-image" />
              </Link>
            </div>
            <div className="nav-links">
              <Link to="/features">Features</Link>
              <Link to="/articles" className="active">Protection Guide</Link>
              <Link to="/about">About</Link>
              {isAuthenticated ? (
                <Link to="/dashboard" className="btn-secondary">Dashboard</Link>
              ) : (
                <>
                  <Link to="/login" className="btn-secondary">Login</Link>
                  <Link to="/signup" className="btn-primary">Sign Up</Link>
                </>
              )}
            </div>
          </div>
        </nav>
      </header>

      {/* Main Content - Pushed below header */}
      <main className="content-wrapper">
        <div className="articles-container">
          <div className="articles-header">
            <h1>Self-Protection Resource Center</h1>
            <p>Empowering knowledge to recognize, prevent, and respond to harassment</p>
          </div>

          <div className="articles-grid">
            {articles.map(article => (
              <article key={article.id} className="article-card">
                {/* Article content */}
              </article>
            ))}
          </div>

          <section className="emergency-resources">
            {/* Emergency resources content */}
          </section>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        {/* Footer content */}
      </footer>
    </div>
  );
};

export default ArticlesPage;
