import React, { useEffect, useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import { Container, Row, Col, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const AccountPage = () => {
  const [incidents, setIncidents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem('reports') || '[]');
    setIncidents(stored);
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'text-primary';
      case 'Complete': return 'text-success';
      case 'In-Process': return 'text-warning';
      default: return 'text-secondary';
    }
  };

  const handleDelete = (index) => {
    if (window.confirm('Delete this report permanently?')) {
      const updated = incidents.filter((_, i) => i !== index);
      localStorage.setItem('reports', JSON.stringify(updated));
      setIncidents(updated);
    }
  };

  const handleEdit = (index) => {
    localStorage.setItem('reportToEdit', JSON.stringify({ data: incidents[index], index }));
    navigate('/report-accident');
  };

  const handleMarkComplete = (index) => {
    const updated = [...incidents];
    updated[index].status = 'Complete';
    updated[index].adminComment = 'we talked with ------------------\n------------------';
    setIncidents(updated);
    localStorage.setItem('reports', JSON.stringify(updated));
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />

      <Container className="flex-grow-1" style={{ paddingTop: '120px' }}>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="fw-bold">Hello, Lana</h2>
          <Button variant="outline-dark" onClick={() => navigate('/report-accident')}>
            Report It, Now!
          </Button>
        </div>

        <Row className="gy-4">
          {incidents.map((incident, index) => (
            <Col key={index} xs={12} sm={6} md={4}>
              <div className="position-relative p-3 bg-light rounded shadow-sm">
                <button
                  className="btn btn-sm btn-danger position-absolute top-0 end-0 m-2 rounded-circle"
                  onClick={() => handleDelete(index)}
                  style={{ width: '30px', height: '30px', fontWeight: 'bold', lineHeight: '1' }}
                >
                  ×
                </button>

                <h5 className="fw-semibold">{incident.title || 'Title of the incident'}</h5>
                <p className="mb-1 text-muted" style={{ fontSize: '0.9rem' }}>
                  {incident.submissionDate}
                </p>
                <p className={`mb-2 ${getStatusColor(incident.status || 'Pending')} fw-semibold`} style={{ fontSize: '0.9rem' }}>
                  {incident.status || 'Pending'}
                </p>

                <button
                  className="btn btn-link text-decoration-none text-dark fw-semibold p-0"
                  style={{ fontSize: '0.9rem' }}
                  onClick={() => handleEdit(index)}
                >
                  Edit the submission →
                </button>

                {incident.status !== 'Complete' && (
                  <button
                    className="btn btn-sm btn-outline-success mt-2"
                    onClick={() => handleMarkComplete(index)}
                  >
                    Mark as Complete
                  </button>
                )}
              </div>
            </Col>
          ))}
        </Row>
      </Container>

      <Footer />
    </div>
  );
};

export default AccountPage;
