import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = () => {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const handleGetStarted = () => {
    navigate(isAuthenticated ? '/dashboard' : '/login');
  };

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header isAuthenticated={isAuthenticated} />

      <section className="flex-grow-1 d-flex align-items-center bg-light py-5">
        <div className="container">
          <div className="row align-items-center">
            {/* Text Content */}
            <div className="col-md-6 text-center text-md-start mb-4 mb-md-0">
              <h1 className="fw-bold display-5">
                Harassment isn't your shame, it's a crime
              </h1>
              <p className="lead text-muted mt-3 mb-4">
                Every second you wait, they get away with it. Report now.
              </p>
              <button
                className="btn btn-dark btn-lg rounded-pill px-4"
                onClick={handleGetStarted}
              >
                {isAuthenticated ? 'Go to Dashboard' : "Get Started – It's Free"}
              </button>
            </div>

            {/* Illustration Image */}
            <div className="col-md-6 text-center">
              <img
                src="/images/speakup.png"
                alt="SpeakUpHer illustration"
                className="img-fluid"
                style={{ maxHeight: '350px' }}
              />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default HomePage;
