import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from './Header';
import Footer from './Footer';

const AccountPage = () => {
  const incidents = [
    { id: 1, title: "Incident A", date: "2024-04-01", status: "Pending" },
    { id: 2, title: "Incident B", date: "2024-03-20", status: "Complete" },
    { id: 3, title: "Incident C", date: "2024-03-15", status: "In-Process" }
  ];

  const getStatusClass = (status) => {
    switch (status) {
      case 'Pending':
        return 'bg-warning text-dark';
      case 'Complete':
        return 'bg-success text-white';
      case 'In-Process':
        return 'bg-primary text-white';
      default:
        return 'bg-secondary text-white';
    }
  };

  return (
    <div>
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />

      <div className="container py-5 mt-5">
        <h2 className="mb-4">Welcome, Lana</h2>

        <div className="card shadow-sm">
          <div className="card-header bg-light">
            <h5 className="mb-0">Your Reported Incidents</h5>
          </div>

          <ul className="list-group list-group-flush">
            {incidents.map((incident) => (
              <li
                key={incident.id}
                className="list-group-item d-flex justify-content-between align-items-center"
              >
                <div>
                  <h6 className="mb-1 fw-bold">{incident.title}</h6>
                  <small className="text-muted">Submitted: {incident.date}</small>
                </div>
                <span className={`badge rounded-pill px-3 py-2 ${getStatusClass(incident.status)}`}>
                  {incident.status}
                </span>
              </li>
            ))}
          </ul>

          <div className="card-footer text-center bg-white">
            <button className="btn btn-danger fw-bold">Report It, Now!</button>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default AccountPage;
