import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/auth.service';
import FileUpload from './FileUpload';
import '../styles/Report.css';

const Report = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    incident_date: '',
    incident_time: '',
    location: '',
    submission_date: '',
    incident_type: '',
    description: '',
    witnesses: '',
  });
  const [files, setFiles] = useState([]);
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFilesChange = (selectedFiles) => {
    setFiles(selectedFiles);
  };

  const uploadFiles = async (reportId, token) => {
    const totalFiles = files.length;
    let completedFiles = 0;

    try {
      await Promise.all(files.map(async (file) => {
        const formData = new FormData();
        formData.append('file', file);
        formData.append('report_id', reportId);

        const response = await fetch('http://localhost:5000/api/reports/attachment', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`
          },
          body: formData
        });

        if (!response.ok) {
          throw new Error(`Failed to upload file ${file.name}`);
        }

        completedFiles++;
        setUploadProgress(Math.round((completedFiles / totalFiles) * 100));
      }));
    } catch (error) {
      console.error('Error uploading files:', error);
      throw new Error('Failed to upload one or more files');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsSubmitting(true);
    setUploadProgress(0);

    try {
      const currentUser = authService.getCurrentUser();
      if (!currentUser) {
        navigate('/login');
        return;
      }

      // Submit report
      const response = await fetch('http://localhost:5000/api/reports', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${currentUser.token}`
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.message || 'Failed to submit report');
      }

      const reportData = await response.json();

      // Upload files if any
      if (files.length > 0) {
        await uploadFiles(reportData.report_id, currentUser.token);
      }

      navigate('/dashboard');
    } catch (error) {
      console.error('Error submitting report:', error);
      setError(error.message || 'An error occurred while submitting the report');
      setUploadProgress(0);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="report-container">
      <div className="report-content">
        <h2>Incident Report Form</h2>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="form-group">
              <label>Date of Incident</label>
              <input
                type="date"
                name="incident_date"
                value={formData.incident_date}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Time of Incident</label>
              <input
                type="time"
                name="incident_time"
                value={formData.incident_time}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Location</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                placeholder="e.g. Office Building, 3rd Floor"
                required
              />
            </div>
            <div className="form-group">
              <label>Date of Submission</label>
              <input
                type="date"
                name="submission_date"
                value={formData.submission_date}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-group">
            <label>Type of Incident</label>
            <select
              name="incident_type"
              value={formData.incident_type}
              onChange={handleInputChange}
              required
            >
              <option value="">Select</option>
              <option value="verbal">Verbal</option>
              <option value="physical">Physical</option>
              <option value="sexual">Sexual</option>
              <option value="cyber">Cyber</option>
              <option value="bullying">Bullying</option>
              <option value="stalking">Stalking</option>
              <option value="other">Other</option>
            </select>
          </div>

          <div className="form-group">
            <label>Description</label>
            <textarea
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              rows={4}
              required
              placeholder="Please provide a detailed description of the incident"
            />
          </div>

          <div className="form-group">
            <label>Witnesses</label>
            <input
              type="text"
              name="witnesses"
              value={formData.witnesses}
              onChange={handleInputChange}
              placeholder="Names of any witnesses (optional)"
            />
          </div>

          <div className="form-group">
            <label>Attachments</label>
            <FileUpload onFilesChange={handleFilesChange} maxFileSize={5} />
          </div>

          {uploadProgress > 0 && (
            <div className="upload-progress">
              <div 
                className="progress-bar" 
                style={{ width: `${uploadProgress}%` }}
              ></div>
              <div className="progress-text">{uploadProgress}% uploaded</div>
            </div>
          )}

          <div className="form-actions">
            <button type="submit" disabled={isSubmitting}>
              {isSubmitting ? 'Submitting...' : 'Submit Report'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Report;
