import React, { useEffect, useState } from 'react';
import Header from './Header';
import Footer from './Footer';
import { Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const UserReportView = () => {
  const [report, setReport] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const index = localStorage.getItem('userViewIndex');
    const reports = JSON.parse(localStorage.getItem('reports') || '[]');
    if (index !== null && reports[index]) {
      setReport(reports[index]);
    } else {
      navigate('/account');
    }
  }, [navigate]);

  if (!report) return null;

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header isAuthenticated={true} />

      <Container style={{ paddingTop: '200px', maxWidth: '750px' }}>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="fw-bold mb-0">Your Submitted Report</h2>
          <Button variant="outline-secondary" onClick={() => navigate('/account')}>Back</Button>
        </div>

        <p><strong>Date of Incident:</strong> {report.incidentDate}</p>
        <p><strong>Time of Incident:</strong> {report.incidentTime}</p>
        <p><strong>Location:</strong> {report.location}</p>
        <p><strong>Date of Submission:</strong> {report.submissionDate}</p>
        <p><strong>Type of Incident:</strong> {report.incidentType}</p>
        <p><strong>Description:</strong><br />{report.description}</p>
        <p><strong>Witnesses:</strong> {report.witnesses}</p>

        {report.adminComment && report.status === 'Complete' && (
          <div className="mt-4">
            <h5 className="fw-bold">Admin's Comment:</h5>
            <p className="fst-italic text-muted">{report.adminComment}</p>
          </div>
        )}
      </Container>

      <Footer />
    </div>
  );
};

export default UserReportView;
