import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/auth.service';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    if (!currentUser) {
      navigate('/login');
      return;
    }
    
    // Fetch user details from database
    const fetchUserDetails = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/users/${currentUser.user_id}`, {
          headers: {
            'Authorization': `Bearer ${currentUser.token}`
          }
        });
        if (response.ok) {
          const userData = await response.json();
          setUser(userData);
        } else {
          console.error('Failed to fetch user details');
          setUser(currentUser);
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
        setUser(currentUser);
      }
    };

    fetchUserDetails();
    fetchUserReports(currentUser.user_id);
  }, [navigate]);

  const fetchUserReports = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reports/user/${userId}`, {
        headers: {
          'Authorization': `Bearer ${authService.getCurrentUser().token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setReports(data);
      }
      setLoading(false);
    } catch (error) {
      console.error('Error fetching reports:', error);
      setLoading(false);
    }
  };

  const handleReportClick = (reportId) => {
    navigate(`/report/${reportId}`);
  };

  const handleNewReport = () => {
    navigate('/report');
  };

  const handleDeleteReport = async (reportId, e) => {
    e.stopPropagation();
    // Add delete functionality here
    console.log('Delete report:', reportId);
  };

  if (loading) {
    return <div className="dashboard-loading">Loading...</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>Hello, {user?.first_name || 'User'}</h1>
        <button onClick={handleNewReport} className="report-button">
          Report It, Now!
        </button>
      </div>

      {reports.length > 0 ? (
        <div className="reports-grid">
          {reports.map(report => (
            <div 
              key={report.report_id} 
              className="report-card"
              onClick={() => handleReportClick(report.report_id)}
            >
              <button 
                className="close-button"
                onClick={(e) => handleDeleteReport(report.report_id, e)}
                aria-label="Delete report"
              >
                <svg viewBox="0 0 24 24">
                  <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                </svg>
              </button>
              <h3>{report.title || 'Title of the incident'}</h3>
              <div className="report-details">
                <p>Date of submission: {new Date(report.submission_date).toLocaleDateString()}</p>
                <span className={`status ${report.status.toLowerCase()}`}>
                  {report.status}
                </span>
              </div>
              <div className="report-actions">
                <button className="edit-button">
                  Edit the submission →
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-reports">
          <p>You haven't submitted any reports yet.</p>
          <button onClick={handleNewReport} className="start-report-button">
            Submit your first report
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
