import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AccountPage.css';

const AccountPage = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    jobTitle: '',
    industry: '',
    yearsExperience: '',
    skills: '',
    linkedinUrl: '',
    bio: ''
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    // Check authentication
    // const isAuthenticated = localStorage.getItem('isAuthenticated');
    // if (!isAuthenticated) {
    //   navigate('/login');
    //   return;
    // }

    // Simulate loading user data
    const fetchUserData = async () => {
      try {
        await new Promise(resolve => setTimeout(resolve, 800));
        
        // Mock user data - replace with real API call
        const mockUserData = {
          firstName: 'John',
          lastName: 'Doe',
          email: 'john@example.com',
          company: 'Tech Solutions Inc.',
          jobTitle: 'Senior Developer',
          industry: 'Information Technology',
          yearsExperience: '5',
          skills: 'React, Node.js, TypeScript',
          linkedinUrl: 'https://linkedin.com/in/johndoe',
          bio: 'Passionate developer with experience in building web applications.'
        };
        
        setFormData(mockUserData);
      } catch (error) {
        setError('Failed to load user data');
      } finally {
        setIsLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Validate LinkedIn URL
    if (formData.linkedinUrl && !formData.linkedinUrl.includes('linkedin.com')) {
      setError('Please enter a valid LinkedIn URL');
      return;
    }

    setIsLoading(true);

    try {
      // Simulate API call to save data
      await new Promise(resolve => setTimeout(resolve, 1000));
      console.log('Profile updated:', formData);
      setIsEditing(false);
    } catch (error) {
      setError('Failed to update profile');
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    // Reset form to original values
    setIsEditing(false);
    // In a real app, you might want to re-fetch the original data
  };

  if (isLoading) {
    return (
      <div className="account-loading">
        <div className="spinner"></div>
        <p>Loading your profile...</p>
      </div>
    );
  }

  return (
    <div className="account-container">
      <div className="account-header">
        <h1>Account Settings</h1>
        {!isEditing && (
          <button 
            onClick={() => setIsEditing(true)} 
            className="edit-btn"
          >
            Edit Profile
          </button>
        )}
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit} className="account-form">
        <div className="form-section">
          <h2>Personal Information</h2>
          <div className="form-row">
            <div className="form-group">
              <label>First Name</label>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleChange}
                disabled={!isEditing}
                required
              />
            </div>
            <div className="form-group">
              <label>Last Name</label>
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleChange}
                disabled={!isEditing}
                required
              />
            </div>
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              disabled={!isEditing}
              required
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Work Information</h2>
          <div className="form-row">
            <div className="form-group">
              <label>Company</label>
              <input
                type="text"
                name="company"
                value={formData.company}
                onChange={handleChange}
                disabled={!isEditing}
              />
            </div>
            <div className="form-group">
              <label>Job Title</label>
              <input
                type="text"
                name="jobTitle"
                value={formData.jobTitle}
                onChange={handleChange}
                disabled={!isEditing}
              />
            </div>
          </div>
          <div className="form-row">
            <div className="form-group">
              <label>Industry</label>
              <input
                type="text"
                name="industry"
                value={formData.industry}
                onChange={handleChange}
                disabled={!isEditing}
              />
            </div>
            <div className="form-group">
              <label>Years of Experience</label>
              <select
                name="yearsExperience"
                value={formData.yearsExperience}
                onChange={handleChange}
                disabled={!isEditing}
              >
                <option value="">Select</option>
                <option value="0-1">0-1 years</option>
                <option value="1-3">1-3 years</option>
                <option value="3-5">3-5 years</option>
                <option value="5-10">5-10 years</option>
                <option value="10+">10+ years</option>
              </select>
            </div>
          </div>
          <div className="form-group">
            <label>Skills (comma separated)</label>
            <input
              type="text"
              name="skills"
              value={formData.skills}
              onChange={handleChange}
              disabled={!isEditing}
              placeholder="React, JavaScript, Project Management"
            />
          </div>
        </div>

        <div className="form-section">
          <h2>Professional Profile</h2>
          <div className="form-group">
            <label>LinkedIn Profile URL</label>
            <input
              type="url"
              name="linkedinUrl"
              value={formData.linkedinUrl}
              onChange={handleChange}
              disabled={!isEditing}
              placeholder="https://linkedin.com/in/yourprofile"
            />
          </div>
          <div className="form-group">
            <label>Professional Bio</label>
            <textarea
              name="bio"
              value={formData.bio}
              onChange={handleChange}
              disabled={!isEditing}
              rows="4"
            />
          </div>
        </div>

        {isEditing && (
          <div className="form-actions">
            <button 
              type="button" 
              onClick={handleCancel}
              className="cancel-btn"
            >
              Cancel
            </button>
            <button 
              type="submit" 
              disabled={isLoading}
              className="save-btn"
            >
              {isLoading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        )}
      </form>

      {formData.linkedinUrl && (
        <div className="linkedin-preview">
          <h3>Your LinkedIn Profile</h3>
          <div className="preview-container">
            <p>Profile URL: <a href={formData.linkedinUrl} target="_blank" rel="noopener noreferrer">{formData.linkedinUrl}</a></p>
            <button 
              onClick={() => window.open(formData.linkedinUrl, '_blank')}
              className="view-linkedin-btn"
            >
              View on LinkedIn
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AccountPage;
