import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/Footer.css';
import '../CSS/Header.css';
import '../CSS/PolicyPage.css';
import '../CSS/HomePage.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const PolicyPage = () => {
  const [activeSection, setActiveSection] = useState('privacy');
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const policySections = {
    privacy: {
      title: "Privacy Policy",
      content: [
        {
          heading: "Information Collection",
          text: "We collect personal information when you create an account, submit reports, or contact us. This may include your name, email address, and incident details."
        },
        {
          heading: "Data Usage",
          text: "Your information is used solely to provide our services, improve user experience, and ensure platform safety. We never sell your data to third parties."
        },
        {
          heading: "Confidentiality",
          text: "All harassment reports are treated with strict confidentiality. Only authorized personnel have access to sensitive information."
        }
      ]
    },
    terms: {
      title: "Terms of Service",
      content: [
        {
          heading: "User Responsibilities",
          text: "Users must provide accurate information and not misuse the platform for false reports or harassment."
        },
        {
          heading: "Content Ownership",
          text: "By submitting content, you grant us license to use it for providing services, but retain all ownership rights."
        },
        {
          heading: "Account Termination",
          text: "We reserve the right to terminate accounts violating our community guidelines or engaging in abusive behavior."
        }
      ]
    },
    reporting: {
      title: "Reporting Guidelines",
      content: [
        {
          heading: "What to Report",
          text: "Include all forms of harassment: verbal, physical, sexual, digital, and workplace discrimination."
        },
        {
          heading: "Evidence Submission",
          text: "Provide any available evidence (messages, photos, witness statements) while ensuring it doesn't violate others' privacy."
        },
        {
          heading: "False Reports",
          text: "Knowingly submitting false reports may result in legal consequences and permanent account suspension."
        }
      ]
    }
  };

  return (
    <div className="home-page">
        <Header isAuthenticated={isAuthenticated} />

      <main className="policy-main">
        <div className="policy-container">
          <h1> </h1>
          
          <div className="policy-nav">
            <button 
              className={`policy-nav-btn ${activeSection === 'privacy' ? 'active' : ''}`}
              onClick={() => setActiveSection('privacy')}
            >
              Privacy Policy
            </button>
            <button 
              className={`policy-nav-btn ${activeSection === 'terms' ? 'active' : ''}`}
              onClick={() => setActiveSection('terms')}
            >
              Terms of Service
            </button>
            <button 
              className={`policy-nav-btn ${activeSection === 'reporting' ? 'active' : ''}`}
              onClick={() => setActiveSection('reporting')}
            >
              Reporting Guidelines
            </button>
          </div>

          <section className="policy-content">
            <h2>{policySections[activeSection].title}</h2>
            <div className="policy-articles">
              {policySections[activeSection].content.map((item, index) => (
                <article key={index} className="policy-article">
                  <h3>{item.heading}</h3>
                  <p>{item.text}</p>
                </article>
              ))}
            </div>
          </section>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default PolicyPage;
