import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function AccountPage() {
  const incidents = [
    { id: 1, title: "Title of the incident", date: "Date of submission", status: "Pending" },
    { id: 2, title: "Title of the incident", date: "Date of submission", status: "Complete" },
    { id: 3, title: "Title of the incident", date: "Date of submission", status: "In-Process" }
  ];

  const getStatusClass = (status) => {
    switch(status) {
      case 'Pending':
        return 'bg-warning text-dark';
      case 'Complete':
        return 'bg-success text-white';
      case 'In-Process':
        return 'bg-primary text-white';
      default:
        return 'bg-secondary text-white';
    }
  };

  return (
    <div className="container-fluid min-vh-100">
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3 bg-dark text-white p-4">
          <h2 className="border-bottom pb-3 mb-4">Products</h2>
          <nav className="nav flex-column mb-auto">
            <a className="nav-link active text-white fw-bold" href="#">Account</a>
            <a className="nav-link text-white" href="#">Report Now!</a>
          </nav>
          <div className="mt-auto pt-4">
            <nav className="nav flex-column">
              <a className="nav-link text-white" href="#">Support</a>
              <a className="nav-link text-white" href="#">Privacy Policy</a>
            </nav>
          </div>
        </div>

        {/* Main Content */}
        <div className="col-md-9 p-4">
          <header className="mb-4">
            <h1>Hello, Lana</h1>
          </header>

          <div className="card shadow-sm mb-4">
            <div className="card-body p-0">
              {incidents.map((incident) => (
                <div key={incident.id} className="d-flex justify-content-between align-items-center p-3 border-bottom">
                  <div>
                    <h5 className="mb-1 fw-bold">{incident.title}</h5>
                    <small className="text-muted">{incident.date}</small>
                  </div>
                  <span className={`badge rounded-pill ${getStatusClass(incident.status)}`}>
                    {incident.status}
                  </span>
                </div>
              ))}

              <div className="text-center p-3">
                <button className="btn btn-danger fw-bold">Report It, Now!</button>
              </div>
            </div>
          </div>

          <nav className="nav">
            <a className="nav-link text-primary" href="#">Report Now!</a>
            <a className="nav-link text-primary" href="#">Account</a>
            <a className="nav-link text-primary" href="#">Support</a>
            <a className="nav-link text-primary" href="#">Privacy Policy</a>
          </nav>
        </div>
      </div>
    </div>
  );
}

export default AccountPage;
