import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './AccountPage.css';
import './HomePage.css';

const AccountPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    jobTitle: '',
    industry: '',
    yearsExperience: '',
    skills: '',
    linkedinUrl: '',
    bio: ''
  });
  const [isEditing, setIsEditing] = useState(true); // Start in edit mode since fields are empty

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data:', formData);
    setIsEditing(false);
  };

  return (
    <div className="account-page">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="navbar-container">
          
          <div className="container">
                    <div className="logo">YourApp</div>
                    <div className="nav-links">
                      <Link to="/features">Features</Link>
                      <Link to="/pricing">Pricing</Link>
                      <Link to="/about">About</Link>
                      {/* { {isAuthenticated ? ( */}
                      {/* ) : ( } */}
                        <>
                          <Link to="/login" className="btn-secondary">Login</Link>
                          <Link to="/signup" className="btn-primary">Sign Up</Link>
                        </>
                       {/* )} */}
                    </div>
                  </div>

        </div>
      </nav>

      <div className="account-content">
        <div className="account-container">
          <h1>Professional Profile</h1>
          <p>Please fill in your professional information</p>

          <form onSubmit={handleSubmit} className="account-form">
            <div className="form-section">
              <h3>Personal Information</h3>
              <div className="form-row">
                <div className="form-group">
                  <label>First Name *</label>
                  <input
                    type="text"
                    name="firstName"
                    value={formData.firstName}
                    onChange={handleChange}
                    disabled={!isEditing}
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Last Name *</label>
                  <input
                    type="text"
                    name="lastName"
                    value={formData.lastName}
                    onChange={handleChange}
                    disabled={!isEditing}
                    required
                  />
                </div>
              </div>
              <div className="form-group">
                <label>Email *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  disabled={!isEditing}
                  required
                />
              </div>
            </div>

            <div className="form-section">
              <h3>Work Information</h3>
              <div className="form-row">
                <div className="form-group">
                  <label>Company</label>
                  <input
                    type="text"
                    name="company"
                    value={formData.company}
                    onChange={handleChange}
                    disabled={!isEditing}
                  />
                </div>
                <div className="form-group">
                  <label>Job Title</label>
                  <input
                    type="text"
                    name="jobTitle"
                    value={formData.jobTitle}
                    onChange={handleChange}
                    disabled={!isEditing}
                  />
                </div>
              </div>
              <div className="form-group">
                <label>Industry</label>
                <input
                  type="text"
                  name="industry"
                  value={formData.industry}
                  onChange={handleChange}
                  disabled={!isEditing}
                />
              </div>
              <div className="form-group">
                <label>Years of Experience</label>
                <input
                  type="number"
                  name="yearsExperience"
                  value={formData.yearsExperience}
                  onChange={handleChange}
                  disabled={!isEditing}
                  min="0"
                />
              </div>
              <div className="form-group">
                <label>Skills (comma separated)</label>
                <input
                  type="text"
                  name="skills"
                  value={formData.skills}
                  onChange={handleChange}
                  disabled={!isEditing}
                  placeholder="React, JavaScript, Project Management"
                />
              </div>
            </div>

            <div className="form-section">
              <h3>Professional Links</h3>
              <div className="form-group">
                <label>LinkedIn Profile URL</label>
                <input
                  type="url"
                  name="linkedinUrl"
                  value={formData.linkedinUrl}
                  onChange={handleChange}
                  disabled={!isEditing}
                  placeholder="https://linkedin.com/in/yourprofile"
                />
              </div>
            </div>

            <div className="form-section">
              <h3>About You</h3>
              <div className="form-group">
                <label>Bio</label>
                <textarea
                  name="bio"
                  value={formData.bio}
                  onChange={handleChange}
                  disabled={!isEditing}
                  rows="4"
                  placeholder="Tell us about your professional background..."
                />
              </div>
            </div>

            {isEditing ? (
              <div className="form-actions">
                <button type="submit" className="save-btn">
                  Save Profile
                </button>
                <button 
                  type="button" 
                  onClick={() => {
                    setFormData({
                      firstName: '',
                      lastName: '',
                      email: '',
                      company: '',
                      jobTitle: '',
                      industry: '',
                      yearsExperience: '',
                      skills: '',
                      linkedinUrl: '',
                      bio: ''
                    });
                  }}
                  className="cancel-btn"
                >
                  Clear Form
                </button>
              </div>
            ) : (
              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={() => setIsEditing(true)}
                  className="edit-btn"
                >
                  Edit Profile
                </button>
              </div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};

export default AccountPage;
