import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
// import './AdminDashboard.css';

const AdminDashboard = () => {
  const [reports, setReports] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedReports = JSON.parse(localStorage.getItem('reports') || '[]');
    setReports(storedReports);
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending': return 'text-primary';
      case 'Complete': return 'text-success';
      case 'In-Process': return 'text-warning';
      default: return 'text-secondary';
    }
  };

  const handleOpenReport = (index) => {
    localStorage.setItem('adminReviewIndex', index);
    navigate('/admin-view');
  };

  return (
    <Container style={{ paddingTop: '120px' }}>
      <h2 className="fw-bold mb-4">Hello, Admin</h2>
      <Row className="gy-4">
        {reports.map((report, index) => (
          <Col xs={12} sm={6} md={4} key={index}>
            <div
              className="p-4 bg-light rounded shadow-sm cursor-pointer"
              style={{ borderRadius: '20px' }}
              onClick={() => handleOpenReport(index)}
            >
              <h5 className="fw-bold">Title of the incident</h5>
              <p className="mb-0 small">{report.email || 'No email provided'}</p>
              <p className="mb-2 small text-muted">Date of submission: {report.submissionDate}</p>
              <span className={`fw-semibold ${getStatusColor(report.status || 'Pending')}`}>
                {report.status || 'Pending'}
              </span>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default AdminDashboard;
