// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';

const HomePage = () => {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const handleGetStarted = () => {
    if (isAuthenticated) {
      navigate('/dashboard');
    } else {
      navigate('/login');
    }
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header isAuthenticated={isAuthenticated} />

      <main className="flex-grow">
        <section className="bg-gray-100 py-20">
          <div className="container mx-auto px-6 flex flex-col-reverse lg:flex-row items-center">
            <div className="w-full lg:w-1/2 text-center lg:text-left">
              <h1 className="text-4xl lg:text-5xl font-bold text-gray-800 mb-6">
                Harassment isn't your shame, it's a crime
              </h1>
              <p className="text-lg text-gray-600 mb-6">
                Every second you wait, they get away with it. Report now.
              </p>
              <button
                onClick={handleGetStarted}
                className="bg-blue-600 text-white px-6 py-3 rounded-md text-lg hover:bg-blue-700 transition duration-300"
              >
                {isAuthenticated ? 'Go to Dashboard' : "Get Started - It's Free"}
              </button>
            </div>
            <div className="w-full lg:w-1/2 mb-10 lg:mb-0">
              <img
                src="/images/speakup.png"
                alt="SpeakUpHer illustration"
                className="w-full h-auto"
              />
            </div>
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
};

export default HomePage;
