import React from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import 'bootstrap/dist/css/bootstrap.min.css';

const HomePage = () => {
  const navigate = useNavigate();
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const handleGetStarted = () => {
    navigate(isAuthenticated ? '/dashboard' : '/login');
  };

  return (
    <div className="d-flex flex-column min-vh-100 bg-white" style={{ paddingTop: '120px' }}>
      <Header isAuthenticated={isAuthenticated} />

      {/* Hero Section */}
      <section className="flex-grow-1 d-flex justify-content-center align-items-center py-5">
        <div
          className="container rounded-4 px-4 py-5"
          style={{ backgroundColor: '#1C1C1C', color: 'white' }}
        >
          <div className="row align-items-center">
            {/* Left Text Section */}
            <div className="col-md-6 text-center text-md-start" >
              <h1 className="fw-bold mb-3" style={{ fontSize: '2.5rem' }}>
                Harassment isn't your shame, it's a crime
              </h1>
              <p className="mb-4" style={{ fontSize: '1.25rem' }}>
                Every Second You Wait, They Get Away With It. Report Now
              </p>
              <button
                className="btn btn-light text-dark fw-semibold px-4 py-2 rounded-pill"
                onClick={handleGetStarted}
              >
                {isAuthenticated ? 'Go to Dashboard' : "Get Started - It's Free"}
              </button>
            </div>

            {/* Right Image Section */}
            <div className="col-md-6 text-center mt-4 mt-md-0">
              <img
                src="/images/speakup.png"
                alt="Speak Out Illustration"
                className="img-fluid"
                style={{ maxHeight: '300px' }}
              />
            </div>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default HomePage;
