import React, { useState } from 'react';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/AccountPage.css';

const AccountPage = () => {
  const [profile, setProfile] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    jobTitle: '',
    industry: '',
    yearsExperience: '',
    skills: '',
    linkedinUrl: '',
    bio: ''
  });
  const [editMode, setEditMode] = useState(true);

  const handleInput = (e) => {
    const { name, value } = e.target;
    setProfile({
      ...profile,
      [name]: value
    });
  };

  const saveProfile = (e) => {
    e.preventDefault();
    setEditMode(false);
  };

  const clearForm = () => {
    setProfile({
      firstName: '',
      lastName: '',
      email: '',
      company: '',
      jobTitle: '',
      industry: '',
      yearsExperience: '',
      skills: '',
      linkedinUrl: '',
      bio: ''
    });
  };

  return (
    <div className="page-wrapper">
      <Header isLoggedIn={localStorage.getItem('isAuthenticated')} />

      <div className="account-page">
        <div className="profile-form">
          <h1>Your Profile</h1>
          <p>Update your information</p>

          <form onSubmit={saveProfile}>
            <div className="form-section">
              <h2>Personal Info</h2>
              <div className="input-row">
                <div className="input-field">
                  <label>First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={profile.firstName}
                    onChange={handleInput}
                    disabled={!editMode}
                    required
                  />
                </div>
                <div className="input-field">
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={profile.lastName}
                    onChange={handleInput}
                    disabled={!editMode}
                    required
                  />
                </div>
              </div>
              <div className="input-field">
                <label>Email</label>
                <input
                  type="email"
                  name="email"
                  value={profile.email}
                  onChange={handleInput}
                  disabled={!editMode}
                  required
                />
              </div>
              <div className="input-field">
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={profile.lastName}
                    onChange={handleInput}
                    disabled={!editMode}
                    required
                  />
                </div>
            </div>

            <div className="form-section">
              <h2>Work Info</h2>
              <div className="input-row">
                <div className="input-field">
                  <label>Company</label>
                  <input
                    type="text"
                    name="company"
                    value={profile.company}
                    onChange={handleInput}
                    disabled={!editMode}
                  />
                </div>
                <div className="input-field">
                  <label>Job Title</label>
                  <input
                    type="text"
                    name="jobTitle"
                    value={profile.jobTitle}
                    onChange={handleInput}
                    disabled={!editMode}
                  />
                </div>
              </div>
              <div className="input-field">
                <label>Industry</label>
                <input
                  type="text"
                  name="industry"
                  value={profile.industry}
                  onChange={handleInput}
                  disabled={!editMode}
                />
              </div>
              <div className="input-field">
                <label>Experience (years)</label>
                <input
                  type="number"
                  name="yearsExperience"
                  value={profile.yearsExperience}
                  onChange={handleInput}
                  disabled={!editMode}
                  min="0"
                />
              </div>
              <div className="input-field">
                <label>Skills</label>
                <input
                  type="text"
                  name="skills"
                  value={profile.skills}
                  onChange={handleInput}
                  disabled={!editMode}
                  placeholder="Separate with commas"
                />
              </div>
            </div>

            <div className="form-section">
              <h2>Links</h2>
              <div className="input-field">
                <label>LinkedIn</label>
                <input
                  type="url"
                  name="linkedinUrl"
                  value={profile.linkedinUrl}
                  onChange={handleInput}
                  disabled={!editMode}
                  placeholder="https://linkedin.com/in/you"
                />
              </div>
            </div>

            <div className="form-section">
              <h2>About You</h2>
              <div className="input-field">
                <label>Bio</label>
                <textarea
                  name="bio"
                  value={profile.bio}
                  onChange={handleInput}
                  disabled={!editMode}
                  rows="4"
                />
              </div>
            </div>

            {editMode ? (
              <div className="form-buttons">
                <button type="submit" className="save-button">
                  Save
                </button>
                <button type="button" onClick={clearForm} className="cancel-button">
                  Clear
                </button>
              </div>
            ) : (
              <div className="form-buttons">
                <button type="button" onClick={() => setEditMode(true)} className="edit-button">
                  Edit
                </button>
              </div>
            )}
          </form>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default AccountPage;
