// frontend/pages/Dashboard.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/auth.service';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchUserDataAndReports = async () => {
      try {
        const currentUser = authService.getCurrentUser();
        if (!currentUser) {
          navigate('/login');
          return;
        }

        // Fetch fresh user data from the server
        const response = await fetch('http://localhost:5000/api/users/me', {
          headers: {
            'Authorization': `Bearer ${currentUser.token}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUser(userData);
          // After getting user data, fetch their reports
          fetchUserReports(userData.user_id);
        } else {
          throw new Error('Failed to fetch user data');
        }
      } catch (error) {
        console.error('Error:', error);
        setError('Failed to load reports');
        setLoading(false);
      }
    };

    fetchUserDataAndReports();
  }, [navigate]);

  const fetchUserReports = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reports/user/${userId}`, {
        headers: {
          'Authorization': `Bearer ${authService.getCurrentUser().token}`
        }
      });
      const data = await response.json();
      if (response.ok) {
        setReports(data);
      }
      setLoading(false);
    } catch (error) {
      console.error('Error fetching reports:', error);
      setError('Failed to load reports');
      setLoading(false);
    }
  };

  const handleReportClick = (reportId) => {
    navigate(`/report/${reportId}`);
  };

  const handleNewReport = () => {
    navigate('/report');
  };

  const handleEditReport = (reportId) => {
    navigate(`/report/edit/${reportId}`);
  };

  const handleDeleteReport = async (reportId, e) => {
    e.stopPropagation();
    try {
      const response = await fetch(`http://localhost:5000/api/reports/${reportId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${authService.getCurrentUser().token}`
        }
      });

      if (response.ok) {
        setReports(reports.filter(report => report.report_id !== reportId));
      }
    } catch (error) {
      console.error('Error deleting report:', error);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const formatTime = (timeString) => {
    return timeString ? timeString.slice(0, 5) : ''; // Format HH:mm
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Welcome back, {user?.first_name}! 👋</h1>
          <p className="welcome-subtitle">Here's an overview of your reports</p>
        </div>
        <button onClick={handleNewReport} className="report-button">
          Report It, Now!
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {reports.length > 0 ? (
        <div className="reports-grid">
          {reports.map(report => (
            <div 
              key={report.report_id} 
              className="report-card"
              onClick={() => handleReportClick(report.report_id)}
            >
              <button 
                className="close-button"
                onClick={(e) => handleDeleteReport(report.report_id, e)}
                aria-label="Delete report"
              >
                <svg viewBox="0 0 24 24">
                  <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                </svg>
              </button>
              <div className="report-header">
                <h3>{report.title || 'Title of the incident'}</h3>
                <div className="report-actions">
                  <button
                    onClick={(e) => { e.stopPropagation(); handleEditReport(report.report_id); }}
                    className="edit-button"
                  >
                    Edit
                  </button>
                </div>
              </div>
              <div className="report-details">
                <p><strong>Date of Incident:</strong> {formatDate(report.incident_date)}</p>
                <p><strong>Time of Incident:</strong> {formatTime(report.incident_time)}</p>
                <p><strong>Location:</strong> {report.location}</p>
                <p><strong>Date of Submission:</strong> {formatDate(report.submission_date)}</p>
                <p><strong>Description:</strong> {report.description}</p>
                {report.witnesses && (
                  <p><strong>Witnesses:</strong> {report.witnesses}</p>
                )}
                <span className={`status ${report.status.toLowerCase()}`}>
                  {report.status}
                </span>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-reports">
          <p>You haven't submitted any reports yet.</p>
          <button onClick={handleNewReport} className="start-report-button">
            Submit your first report
          </button>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
