import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import { Container, Form, Button } from 'react-bootstrap';

const AdminView = () => {
  const [report, setReport] = useState(null);
  const [index, setIndex] = useState(null);
  const [inProcess, setInProcess] = useState(false);
  const [comment, setComment] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const idx = localStorage.getItem('adminReviewIndex');
    const allReports = JSON.parse(localStorage.getItem('reports') || '[]');
    if (idx !== null && allReports[idx]) {
      const rep = allReports[idx];
      setReport(rep);
      setComment(rep.adminComment || '');
      setInProcess(rep.status === 'In-Process');
      setIndex(parseInt(idx));
    } else {
      navigate('/admin');
    }
  }, [navigate]);

  const handleSave = () => {
    const allReports = JSON.parse(localStorage.getItem('reports') || '[]');
    const updated = {
      ...report,
      status: 'Complete',
      adminComment: comment
    };
    allReports[index] = updated;
    localStorage.setItem('reports', JSON.stringify(allReports));
    navigate('/admin');
  };

  const handleToggleStatus = () => {
    const allReports = JSON.parse(localStorage.getItem('reports') || '[]');
    const updated = {
      ...report,
      status: inProcess ? 'Pending' : 'In-Process',
      adminComment: ''
    };
    allReports[index] = updated;
    localStorage.setItem('reports', JSON.stringify(allReports));
    setReport(updated);
    setInProcess(!inProcess);
    setComment('');
  };

  if (!report) return null;

  return (
    <div className="d-flex flex-column min-vh-100">
      <Header isAuthenticated={true} />

      <Container className="flex-grow-1 py-5" style={{ paddingTop: '220px', maxWidth: '800px' }}>
        {/* Title + Toggle beside each other */}
        <div className="d-flex align-items-center justify-content-between mb-4">
          <h2 className="fw-bold mb-0">Incident Report Form</h2>
          <Form.Group className="form-check form-switch mb-0">
            <Form.Check
              type="switch"
              id="statusToggle"
              label={inProcess ? 'In-Process' : 'Pending'}
              checked={inProcess}
              onChange={handleToggleStatus}
            />
          </Form.Group>
        </div>

        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Date of Incident</Form.Label>
            <Form.Control type="text" value={report.incidentDate} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Time of Incident</Form.Label>
            <Form.Control type="text" value={report.incidentTime} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Location</Form.Label>
            <Form.Control type="text" value={report.location} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Date of Submission</Form.Label>
            <Form.Control type="text" value={report.submissionDate} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Type of Incident</Form.Label>
            <Form.Control type="text" value={report.incidentType} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control as="textarea" rows={5} value={report.description} disabled />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Witnesses</Form.Label>
            <Form.Control type="text" value={report.witnesses} disabled />
          </Form.Group>

          {report.files && (
            <Form.Group className="mb-4">
              <Form.Label>Uploaded Files</Form.Label>
              <Button variant="dark" disabled>Download Documents</Button>
            </Form.Group>
          )}

          <Form.Group className="mb-4">
            <Form.Label>Add the Updates</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              placeholder="e.g. We contacted the team, etc."
              disabled={!inProcess}
            />
          </Form.Group>

          <div className="d-flex justify-content-end">
            <Button
              variant="success"
              onClick={handleSave}
              disabled={!inProcess}
            >
              Complete
            </Button>
          </div>
        </Form>
      </Container>

      <Footer />
    </div>
  );
};

export default AdminView;
