import React from 'react';
import Header from './Header';
import Footer from './Footer';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Row, Col, Button } from 'react-bootstrap';

const AccountPage = () => {
  const incidents = [
    { id: 1, title: "Title of the incident", date: "2024-04-01", status: "Pending" },
    { id: 2, title: "Title of the incident", date: "2024-03-20", status: "Complete" },
    { id: 3, title: "Title of the incident", date: "2024-03-15", status: "In-Process" }
  ];

  const getStatusColor = (status) => {
    switch (status) {
      case 'Pending':
        return 'text-primary';
      case 'Complete':
        return 'text-success';
      case 'In-Process':
        return 'text-warning';
      default:
        return 'text-secondary';
    }
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this incident?')) {
      console.log(`Deleted incident with id: ${id}`);
      // Implement deletion logic here (API call or state update)
    }
  };

  return (
    <div>
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />

      <Container className="mt-5 pt-5" style={{ paddingTop: '100px' }}>
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="fw-bold">Hello, Lana</h2>
          <Button variant="outline-dark">Report It, Now!</Button>
        </div>

        <Row className="gy-4">
          {incidents.map((incident) => (
            <Col key={incident.id} xs={12} sm={6} md={4}>
              <div className="position-relative p-3 bg-light rounded shadow-sm">
                <button
                  className="btn btn-sm btn-danger position-absolute top-0 end-0 m-2 rounded-circle"
                  onClick={() => handleDelete(incident.id)}
                  style={{ width: '30px', height: '30px', fontWeight: 'bold', lineHeight: '1' }}
                >
                  ×
                </button>

                <h5 className="fw-semibold">Title of the incident</h5>
                <p className="mb-1 text-muted" style={{ fontSize: '0.9rem' }}>
                  Date of submission
                </p>
                <p className={`mb-2 ${getStatusColor(incident.status)} fw-semibold`} style={{ fontSize: '0.9rem' }}>
                  {incident.status}
                </p>

                <Link
  to={`/edit/${incident.id}`}
  className="text-decoration-none text-dark fw-semibold"
  style={{ fontSize: '0.9rem' }}
>
  Edit the submission →
</Link>

              </div>
            </Col>
          ))}
        </Row>
      </Container>

      <Footer />
    </div>
  );
};

export default AccountPage;
