import { useState } from 'react';
import { FaPaperPlane, FaUserCircle } from 'react-icons/fa';

export default function Chat() {
  const [messages, setMessages] = useState([
    { id: 1, sender: 'Alex', text: 'Hey! Ready for the study session tomorrow?', time: '10:30 AM' },
    { id: 2, sender: 'You', text: 'Definitely! What time works for you?', time: '10:32 AM' }
  ]);
  const [newMessage, setNewMessage] = useState('');

  const handleSend = () => {
    if (newMessage.trim()) {
      setMessages([
        ...messages,
        { id: messages.length + 1, sender: 'You', text: newMessage, time: 'Now' }
      ]);
      setNewMessage('');
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-1/4 bg-white border-r border-gray-200">
        <div className="p-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold">Study Groups</h2>
        </div>
        <div className="p-2">
          <div className="p-3 hover:bg-gray-100 rounded-lg cursor-pointer">
            <div className="flex items-center">
              <FaUserCircle className="text-gray-500 text-xl mr-3" />
              <div>
                <p className="font-medium">COMPSCI 301 Group</p>
                <p className="text-sm text-gray-500 truncate">Alex: See you at 3pm!</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Chat Header */}
        <div className="p-4 border-b border-gray-200 bg-white">
          <h2 className="text-lg font-semibold">COMPSCI 301 Group</h2>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((msg) => (
            <div
              key={msg.id}
              className={`flex ${msg.sender === 'You' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`max-w-xs md:max-w-md rounded-lg px-4 py-2 ${
                  msg.sender === 'You'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-white border border-gray-200'
                }`}
              >
                {msg.sender !== 'You' && (
                  <p className="font-medium text-sm">{msg.sender}</p>
                )}
                <p>{msg.text}</p>
                <p className={`text-xs mt-1 ${msg.sender === 'You' ? 'text-indigo-200' : 'text-gray-500'}`}>
                  {msg.time}
                </p>
              </div>
            </div>
          ))}
        </div>

        {/* Message Input */}
        <div className="p-4 border-t border-gray-200 bg-white">
          <div className="flex">
            <input
              type="text"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              placeholder="Type a message..."
              className="flex-1 border border-gray-300 rounded-l-lg py-2 px-4 focus:outline-none focus:ring-2 focus:ring-indigo-500"
            />
            <button
              onClick={handleSend}
              className="bg-indigo-600 text-white px-4 rounded-r-lg hover:bg-indigo-700 focus:outline-none"
            >
              <FaPaperPlane />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
