import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './AccidentReport.css';
import './HomePage.css';

const AccidentReport = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    reporterName: '',
    incidentDate: '',
    incidentTime: '',
    location: '',
    incidentType: 'slip',
    description: '',
    injuries: '',
    witnesses: '',
    files: null
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [error, setError] = useState('');
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setFormData(prev => ({
      ...prev,
      files: e.target.files
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');

    if (!formData.reporterName || !formData.incidentDate || !formData.location) {
      setError('Please fill in all required fields');
      setIsSubmitting(false);
      return;
    }

    try {
      const formPayload = new FormData();
      Object.entries(formData).forEach(([key, value]) => {
        if (key === 'files' && value) {
          Array.from(value).forEach(file => {
            formPayload.append('files', file);
          });
        } else if (value) {
          formPayload.append(key, value);
        }
      });

      await new Promise(resolve => setTimeout(resolve, 1500));
      console.log('Form submitted:', formData);
      alert('Accident report submitted successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError('Failed to submit report. Please try again.');
      console.error('Submission error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="home-page">
      {/* Navigation Bar */}
      <nav className="navbar">
        <div className="container">
          <div className="logo nav-links">
            <Link to="/">
              <img src="/images/SpeakUp-Her.png" alt="SpeakUpHer Logo" className="logo-image" />
            </Link>
          </div>
          <div className="nav-links">
            <Link to="/features">Features</Link>
            <Link to="/account">Account</Link>
            <Link to="/about">About</Link>
            {isAuthenticated ? (
              <Link to="/dashboard" className="btn-secondary">Dashboard</Link>
            ) : (
              <>
                <Link to="/login" className="btn-secondary">Login</Link>
                <Link to="/signup" className="btn-primary">Sign Up</Link>
              </>
            )}
          </div>
        </div>
      </nav>

      <div className="accident-report-container">
        <div className="report-header">
          <h1>Incident Report Form</h1>
          <p>Please provide details about the workplace incident</p>
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleSubmit} className="report-form">
          <div className="form-section">
            <h3>Reporter Information</h3>
            <div className="form-group">
              <label>Your Name *</label>
              <input
                type="text"
                name="reporterName"
                value={formData.reporterName}
                onChange={handleChange}
                required
              />
            </div>
          </div>

          <div className="form-section">
            <h3>Incident Details</h3>
            <div className="form-row">
              <div className="form-group">
                <label>Date of Incident *</label>
                <input
                  type="date"
                  name="incidentDate"
                  value={formData.incidentDate}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Time of Incident</label>
                <input
                  type="time"
                  name="incidentTime"
                  value={formData.incidentTime}
                  onChange={handleChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Location *</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                required
                placeholder="Building, floor, or specific area"
              />
            </div>

            <div className="form-group">
              <label>Type of Incident *</label>
              <select
                name="incidentType"
                value={formData.incidentType}
                onChange={handleChange}
                required
              >
                <option value="slip">Slip/Trip/Fall</option>
                <option value="strain">Musculoskeletal Strain</option>
                <option value="struck">Struck By/Against Object</option>
                <option value="chemical">Chemical Exposure</option>
                <option value="equipment">Equipment Mishap</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="form-group">
              <label>Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleChange}
                required
                rows="4"
                placeholder="Provide a detailed description of what happened..."
              />
            </div>

            <div className="form-group">
              <label>Injuries Sustained</label>
              <input
                type="text"
                name="injuries"
                value={formData.injuries}
                onChange={handleChange}
                placeholder="Describe any injuries (if applicable)"
              />
            </div>

            <div className="form-group">
              <label>Witnesses (if any)</label>
              <input
                type="text"
                name="witnesses"
                value={formData.witnesses}
                onChange={handleChange}
                placeholder="Names of witnesses"
              />
            </div>
          </div>

          <div className="form-section">
            <h3>Supporting Documents</h3>
            <div className="form-group">
              <label>Upload Photos/Documents</label>
              <input
                type="file"
                name="files"
                onChange={handleFileChange}
                multiple
                accept="image/*,.pdf,.doc,.docx"
              />
              <small>You can upload multiple files (images, PDFs, or Word docs)</small>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="submit"
              className="submit-btn"
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Submitting...' : 'Submit Report'}
            </button>
            <button
              type="button"
              className="cancel-btn"
              onClick={() => navigate(-1)}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>

      {/* Footer */}
<footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>YourApp</h4>
              <p>Making work easier since 2023</p>
            </div>
            <div className="footer-section">
              <h4>Product</h4>
              <Link to="/articles" className="active">Protection Guide</Link>
              <Link to="/pricing">Pricing</Link>
              <Link to="/integrations">Integrations</Link>
            </div>
            <div className="footer-section">
              <h4>Company</h4>
              <Link to="/about">About Us</Link>
              <Link to="/blog">Blog</Link>
              <Link to="/careers">Careers</Link>
            </div>
            <div className="footer-section">
              <h4>Support</h4>
              <Link to="/privacy">Privacy Policy</Link>
            </div>
          </div>
          <div className="footer-bottom">
            <p>© 2025 YourApp. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default AccidentReport;
