import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import Footer from './Footer';
import 'bootstrap/dist/css/bootstrap.min.css';

const AccidentReport = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    incidentDate: '',
    incidentTime: '',
    location: '',
    submissionDate: '',
    incidentType: '',
    description: '',
    witnesses: '',
    files: null,
  });
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  useEffect(() => {
    const editReport = JSON.parse(localStorage.getItem('reportToEdit'));
    if (editReport) {
      setFormData(editReport.data);
      setEditIndex(editReport.index);
      setIsEditing(true);
      localStorage.removeItem('reportToEdit');
    }
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleFileUpload = (e) => {
    setFormData(prev => ({ ...prev, files: e.target.files }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.incidentDate || !formData.location || !formData.submissionDate || !formData.description) {
      setError('Please fill in all required fields');
      return;
    }

    const stored = JSON.parse(localStorage.getItem('reports') || '[]');

    if (isEditing && editIndex !== null) {
      stored[editIndex] = formData;
    } else {
      stored.push(formData);
    }

    localStorage.setItem('reports', JSON.stringify(stored));
    navigate('/account');
  };

  return (
    <div className="report-page d-flex flex-column min-vh-100">
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />

      <div className="container py-5" style={{ paddingTop: '120px', maxWidth: '750px' }}>
        <h2 className="fw-bold mb-4">Incident Report Form</h2>

        {error && <div className="alert alert-danger text-center">{error}</div>}

        <form onSubmit={handleSubmit}>
          <div className="row mb-3">
            <div className="col-md-6 mb-3">
              <label className="form-label">Date of Incident</label>
              <input type="date" name="incidentDate" value={formData.incidentDate} onChange={handleInputChange} className="form-control" />
            </div>
            <div className="col-md-6">
              <label className="form-label">Time of Incident</label>
              <input type="time" name="incidentTime" value={formData.incidentTime} onChange={handleInputChange} className="form-control" />
            </div>
          </div>

          <div className="row mb-3">
            <div className="col-md-6 mb-3">
              <label className="form-label">Location</label>
              <input type="text" name="location" value={formData.location} onChange={handleInputChange} className="form-control" placeholder="e.g. Office Building, 3rd Floor" />
            </div>
            <div className="col-md-6">
              <label className="form-label">Date of Submission</label>
              <input type="date" name="submissionDate" value={formData.submissionDate} onChange={handleInputChange} className="form-control" />
            </div>
          </div>

          <div className="mb-3">
            <label className="form-label">Type of Incident</label>
            <select name="incidentType" value={formData.incidentType} onChange={handleInputChange} className="form-select">
              <option value="">Select</option>
              <option value="verbal">Verbal</option>
              <option value="physical">Physical</option>
              <option value="sexual">Sexual</option>
              <option value="cyber">Cyber</option>
              <option value="bullying">Bullying</option>
              <option value="stalking">Stalking</option>
              <option value="other">Other</option>
            </select>
          </div>

          <div className="mb-3">
            <label className="form-label">Description</label>
            <textarea name="description" value={formData.description} onChange={handleInputChange} className="form-control" rows={4} />
          </div>

          <div className="mb-3">
            <label className="form-label">Witnesses</label>
            <input type="text" name="witnesses" value={formData.witnesses} onChange={handleInputChange} className="form-control" />
          </div>

          <div className="mb-4">
            <label className="form-label">Upload Images/Documents</label>
            <input type="file" className="form-control" onChange={handleFileUpload} multiple />
          </div>

          <div className="mb-3">
            <p className="text-muted">We talked with --------------------------</p>
          </div>

          <div className="d-flex justify-content-end">
            <button type="submit" className="btn btn-dark px-4 py-2">
              Submit
            </button>
          </div>
        </form>
      </div>

      <Footer />
    </div>
  );
};

export default AccidentReport;
