import React, { useEffect } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../styles/Header.css';
import authService from '../services/auth.service';

const Header = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const currentUser = authService.getCurrentUser();
  const isAdmin = currentUser?.email === 'admin@htu.edu.jo';

  // Debug logs
  useEffect(() => {
    console.log('Header - Current user:', currentUser);
    console.log('Header - Is admin?:', isAdmin);
    console.log('Header - Current location:', location.pathname);
  }, [currentUser, location, isAdmin]);

  const handleLogout = () => {
    authService.logout();
    navigate('/');
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg" fixed="top" className="py-3">
      <Container>
        <Navbar.Brand as={Link} to="/" className="d-flex align-items-center">
          <span className="h4 mb-0">HTUharassment</span>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {currentUser ? (
              // Navigation items for logged-in users
              <>
                {isAdmin ? (
                  // Admin navigation items
                  <>
                    <Nav.Link 
                      as={Link} 
                      to="/admin"
                      className={location.pathname === '/admin' ? 'active' : ''}
                    >
                      Admin Dashboard
                    </Nav.Link>
                    <Nav.Link 
                      as={Link} 
                      to="/admin/reports"
                      className={location.pathname === '/admin/reports' ? 'active' : ''}
                    >
                      Manage Reports
                    </Nav.Link>
                  </>
                ) : (
                  // Regular user navigation items
                  <>
                    <Nav.Link 
                      as={Link} 
                      to="/dashboard"
                      className={location.pathname === '/dashboard' ? 'active' : ''}
                    >
                      Dashboard
                    </Nav.Link>
                    <Nav.Link 
                      as={Link} 
                      to="/report"
                      className={location.pathname === '/report' ? 'active' : ''}
                    >
                      Report
                    </Nav.Link>
                  </>
                )}

                {/* User info display */}
                <Nav.Link className="user-info">
                  Welcome, {isAdmin ? 'Admin' : currentUser.first_name || 'User'}
                </Nav.Link>

                {/* Logout button */}
                <Nav.Link 
                  onClick={handleLogout}
                  className="btn btn-outline-light ms-2"
                >
                  Logout
                </Nav.Link>
              </>
            ) : (
              // Navigation items for non-logged-in users
              <>
                <Nav.Link as={Link} to="/login" className="me-3">Login</Nav.Link>
                <Nav.Link 
                  as={Link} 
                  to="/register" 
                  className="btn btn-primary"
                >
                  Sign Up
                </Nav.Link>
              </>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Header;
