import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import authService from '../services/auth.service';
import '../styles/ViewReport.css';

const ViewReport = () => {
  const navigate = useNavigate();
  const { reportId } = useParams();
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchReport = async () => {
      try {
        const currentUser = authService.getCurrentUser();
        if (!currentUser) {
          navigate('/login');
          return;
        }

        const response = await fetch(`http://localhost:5000/api/reports/${reportId}`, {
          headers: {
            'Authorization': `Bearer ${currentUser.token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch report details');
        }

        const data = await response.json();
        setReport(data);
      } catch (error) {
        console.error('Error:', error);
        setError(error.message || 'Failed to load report details');
      } finally {
        setLoading(false);
      }
    };

    fetchReport();
  }, [reportId, navigate]);

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const handleStatusChange = async (newStatus) => {
    try {
      const response = await fetch(`http://localhost:5000/api/reports/${reportId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authService.getCurrentUser().token}`
        },
        body: JSON.stringify({ status: newStatus })
      });

      if (!response.ok) {
        throw new Error('Failed to update status');
      }

      const updatedReport = await response.json();
      setReport(prev => ({ ...prev, status: updatedReport.status }));
    } catch (error) {
      setError(error.message || 'Failed to update status');
      setTimeout(() => setError(''), 5000);
    }
  };

  if (loading) {
    return (
      <div className="view-report-loading">
        <div className="spinner"></div>
        <p>Loading report details...</p>
      </div>
    );
  }

  if (!report) {
    return (
      <div className="view-report-error">
        <h2>Report Not Found</h2>
        <p>{error}</p>
        <button onClick={() => navigate('/admin')} className="back-button">
          Back to Dashboard
        </button>
      </div>
    );
  }

  return (
    <div className="view-report-container">
      <div className="view-report-header">
        <div className="header-content">
          <h1>{report.incident_type} Incident Report</h1>
          <div className="status-section">
            <label>Status:</label>
            <select
              value={report.status}
              onChange={(e) => handleStatusChange(e.target.value)}
              className={`status-select ${report.status.toLowerCase()}`}
            >
              <option value="pending">Pending</option>
              <option value="in-progress">In Progress</option>
              <option value="resolved">Resolved</option>
            </select>
          </div>
        </div>
        <button onClick={() => navigate('/admin')} className="back-button">
          Back to Dashboard
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="report-details-grid">
        <div className="detail-section">
          <h3>Reporter Information</h3>
          <p><strong>Name:</strong> {report.user_first_name} {report.user_last_name}</p>
          <p><strong>Email:</strong> {report.user_email}</p>
        </div>

        <div className="detail-section">
          <h3>Incident Details</h3>
          <p><strong>Date:</strong> {formatDate(report.incident_date)}</p>
          <p><strong>Time:</strong> {report.incident_time}</p>
          <p><strong>Location:</strong> {report.location}</p>
        </div>

        <div className="detail-section full-width">
          <h3>Description</h3>
          <p className="description">{report.description}</p>
        </div>

        {report.witnesses && (
          <div className="detail-section full-width">
            <h3>Witnesses</h3>
            <p>{report.witnesses}</p>
          </div>
        )}

        <div className="detail-section">
          <h3>Report Information</h3>
          <p><strong>Submission Date:</strong> {formatDate(report.submission_date)}</p>
          <p><strong>Report ID:</strong> {report.report_id}</p>
        </div>
      </div>
    </div>
  );
};

export default ViewReport;
