import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './AccountPage.css';
import './HomePage.css';

const AccountPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    company: '',
    jobTitle: '',
    industry: '',
    yearsExperience: '',
    skills: '',
    linkedinUrl: '',
    bio: ''
  });
  const [isEditing, setIsEditing] = useState(true); // Start in edit mode since fields are empty

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form data:', formData);
    setIsEditing(false);
  };

  return (
    <div className="account-page">
      <div className="account-content">
        {/* Navigation Bar - Simplified without auth check */}
        <nav className="navbar">
          <div className="container">
            <div className="logo">YourApp</div>
            <div className="nav-links">
              <Link to="/">Home</Link>
              <Link to="/features">Features</Link>
              <Link to="/pricing">Pricing</Link>
              <Link to="/about">About</Link>
              <Link to="/dashboard" className="btn-secondary">Dashboard</Link>
              <Link to="/account" className="btn-primary">Account</Link>
            </div>
          </div>
        </nav>

        <div className="account-container">
          <h1>Professional Profile</h1>
          <p>Please fill in your professional information</p>

          {/* Rest of your form remains the same */}
          <form onSubmit={handleSubmit} className="account-form">
            {/* All your form sections and fields */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default AccountPage;
