import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const SignupPage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    if (formData.password.length < 6) {
      setError('Password must be at least 6 characters');
      return;
    }

    setIsLoading(true);

    try {
      const users = JSON.parse(localStorage.getItem('users') || '[]');
      const exists = users.some(u => u.email === formData.email);
      if (exists) {
        setError('Email already exists');
        return;
      }

      users.push({ ...formData });
      localStorage.setItem('users', JSON.stringify(users));
      navigate('/login');
    } catch {
      setError('Something went wrong.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="container d-flex justify-content-center align-items-center min-vh-100">
      <div className="card p-4 shadow w-100" style={{ maxWidth: 450 }}>
        <h2 className="text-center fw-bold mb-4">Create New Account</h2>
        {error && <div className="alert alert-danger">{error}</div>}

        <form onSubmit={handleSubmit}>
          {['firstName', 'lastName', 'email', 'password', 'confirmPassword'].map((field, i) => (
            <div className="mb-3" key={i}>
              <label htmlFor={field} className="form-label text-uppercase fw-semibold">
                {field.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}
              </label>
              <input
                type={field.includes('password') ? 'password' : 'text'}
                className="form-control"
                id={field}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                required
              />
            </div>
          ))}

          <button className="btn btn-dark w-100 rounded-pill" type="submit" disabled={isLoading}>
            {isLoading ? 'Creating...' : 'Sign Up'}
          </button>
        </form>

        <div className="text-center mt-3">
          Already registered? <Link to="/login" className="text-decoration-none">Login</Link>
        </div>
      </div>
    </div>
  );
};

export default SignupPage;
