import React, { useState } from 'react'; // Add this import
import { FaSearch, FaUserPlus, FaFilter } from 'react-icons/fa';
import StudyPartnerCard from '../components/StudyPartnerCard';

export default function Dashboard() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filters, setFilters] = useState({
    course: '',
    availability: ''
  });

  // Mock data
  const studyPartners = [
    {
      id: 1,
      name: 'Alex Johnson',
      course: 'COMPSCI 301',
      availability: 'Weekdays',
      rating: 4.5
    },
    {
      id: 2,
      name: 'Sam Lee',
      course: 'MATH 202',
      availability: 'Weekends',
      rating: 4.2
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <h1 className="text-2xl font-bold text-gray-800 mb-6">Find Study Partners</h1>
        
        {/* Search and Filters */}
        <div className="bg-white p-4 rounded-lg shadow mb-6">
          <div className="flex flex-col md:flex-row gap-4">
            <div className="relative flex-grow">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <FaSearch className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Search by course or name"
                className="block w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md leading-5 bg-white placeholder-gray-500 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <button className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
              <FaFilter className="mr-2" />
              Filters
            </button>
          </div>
        </div>

        {/* Partners Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {studyPartners.map((partner) => (
            <StudyPartnerCard key={partner.id} partner={partner} />
          ))}
        </div>
      </div>
    </div>
  );
}
