import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './PolicyPage.css';
import './HomePage.css';

const PolicyPage = () => {
  const [activeTab, setActiveTab] = useState('privacy');
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  const policies = {
    privacy: {
      title: "Privacy Policy",
      lastUpdated: "Last updated: June 15, 2025",
      sections: [
        {
          heading: "Information Collection",
          content: "We collect personal information when you create an account, submit reports, or contact us. This may include your name, email address, and incident details."
        },
        {
          heading: "Data Usage",
          content: "Your information is used solely to provide our services, improve user experience, and ensure platform safety. We never sell your data to third parties."
        },
        {
          heading: "Confidentiality",
          content: "All harassment reports are treated with strict confidentiality. Only authorized personnel have access to sensitive information."
        }
      ]
    },
    terms: {
      title: "Terms of Service",
      lastUpdated: "Last updated: June 15, 2025",
      sections: [
        {
          heading: "User Responsibilities",
          content: "Users must provide accurate information and not misuse the platform for false reports or harassment."
        },
        {
          heading: "Content Ownership",
          content: "By submitting content, you grant us license to use it for providing services, but retain all ownership rights."
        },
        {
          heading: "Account Termination",
          content: "We reserve the right to terminate accounts violating our community guidelines or engaging in abusive behavior."
        }
      ]
    },
    reporting: {
      title: "Reporting Guidelines",
      lastUpdated: "Last updated: June 15, 2025",
      sections: [
        {
          heading: "What to Report",
          content: "Include all forms of harassment: verbal, physical, sexual, digital, and workplace discrimination."
        },
        {
          heading: "Evidence Submission",
          content: "Provide any available evidence (messages, photos, witness statements) while ensuring it doesn't violate others' privacy."
        },
        {
          heading: "False Reports",
          content: "Knowingly submitting false reports may result in legal consequences and permanent account suspension."
        }
      ]
    }
  };

  return (
    <div className="home-page">
      {/* Navigation Bar */}
      <header className="navbar-container">
        <nav className="navbar">
          <div className="container">
            <div className="logo nav-links">
              <Link to="/">
                <img src="/images/SpeakUp-Her.png" alt="SpeakUpHer Logo" className="logo-image" />
              </Link>
            </div>
            <div className="nav-links">
              <Link to="/features">Features</Link>
              <Link to="/articles">Protection Guide</Link>
              <Link to="/about">About</Link>
              {isAuthenticated ? (
                <Link to="/dashboard" className="btn-secondary">Dashboard</Link>
              ) : (
                <>
                  <Link to="/login" className="btn-secondary">Login</Link>
                  <Link to="/signup" className="btn-primary">Sign Up</Link>
                </>
              )}
            </div>
          </div>
        </nav>
      </header>

      <main className="policy-main">
        <div className="policy-container">
          <h1>Platform Policies</h1>
          
          <div className="policy-tabs">
            <button
              className={`policy-tab ${activeTab === 'privacy' ? 'active' : ''}`}
              onClick={() => setActiveTab('privacy')}
            >
              Privacy Policy
            </button>
            <button
              className={`policy-tab ${activeTab === 'terms' ? 'active' : ''}`}
              onClick={() => setActiveTab('terms')}
            >
              Terms of Service
            </button>
            <button
              className={`policy-tab ${activeTab === 'reporting' ? 'active' : ''}`}
              onClick={() => setActiveTab('reporting')}
            >
              Reporting Guidelines
            </button>
          </div>

          <div className="policy-content">
            <div className="policy-header">
              <h2>{policies[activeTab].title}</h2>
              <p className="last-updated">{policies[activeTab].lastUpdated}</p>
            </div>
            
            <div className="policy-sections">
              {policies[activeTab].sections.map((section, index) => (
                <section key={index} className="policy-section">
                  <h3>{section.heading}</h3>
                  <p>{section.content}</p>
                </section>
              ))}
            </div>
          </div>
        </div>
      </main>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h4>SpeakUpHer</h4>
              <p>Creating safer spaces through empowerment</p>
            </div>
            <div className="footer-section">
              <h4>Legal</h4>
              <Link to="/policy">Policies</Link>
              <Link to="/contact">Contact</Link>
            </div>
            <div className="footer-section">
              <h4>Support</h4>
              <Link to="/faq">FAQ</Link>
              <Link to="/help">Help Center</Link>
            </div>
          </div>
          <div className="footer-bottom">
            <p>Â© 2025 SpeakUpHer. All rights reserved.</p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default PolicyPage;
