import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/AccidentReport.css';

const AccidentReport = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    reporterName: '',
    incidentDate: '',
    incidentTime: '',
    location: '',
    incidentType: 'slip',
    description: '',
    injuries: '',
    witnesses: '',
    files: null
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileUpload = (e) => {
    setFormData({
      ...formData,
      files: e.target.files
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    if (!formData.reporterName || !formData.incidentDate || !formData.location) {
      setError('Please fill in all required fields');
      setIsLoading(false);
      return;
    }

    try {
      await new Promise(resolve => setTimeout(resolve, 1500));
      alert('Report submitted successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError('Submission failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="report-page">
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />
      
      <div className="report-form-container">
        <h1 className="form-title">SpeakUpFor</h1>
        
        <div className="form-nav">
          <span className="active">Report</span>
          <span>Protection Guide</span>
          <span>Account</span>
          <span>Login</span>
          <span>Sign Up</span>
        </div>

        <div className="form-header">
        </div>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleFormSubmit}>
          <fieldset className="form-section">
            <legend>Reporter Information</legend>
            <div className="input-group">
              <label>Your Name *</label>
              <input
                type="text"
                name="reporterName"
                value={formData.reporterName}
                onChange={handleInputChange}
                required
              />
            </div>
          </fieldset>

          {/* Rest of your form sections remain the same */}
          <fieldset className="form-section">
            <legend>Incident Details</legend>
            <div className="input-row">
              <div className="input-group">
                <label>Date *</label>
                <input
                  type="date"
                  name="incidentDate"
                  value={formData.incidentDate}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="input-group">
                <label>Time</label>
                <input
                  type="time"
                  name="incidentTime"
                  value={formData.incidentTime}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="input-group">
              <label>Location *</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
                placeholder="Building, floor, or specific area"
              />
            </div>

            <div className="input-group">
              <label>Incident Type *</label>
              <select
                name="incidentType"
                value={formData.incidentType}
                onChange={handleInputChange}
                required
              >
                <option value="slip">Slip/Trip/Fall</option>
                <option value="strain">Muscle Strain</option>
                <option value="struck">Struck By Object</option>
                <option value="chemical">Chemical Exposure</option>
                <option value="equipment">Equipment Problem</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="input-group">
              <label>Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
                placeholder="What happened..."
              />
            </div>

            <div className="input-group">
              <label>Injuries (if any)</label>
              <input
                type="text"
                name="injuries"
                value={formData.injuries}
                onChange={handleInputChange}
              />
            </div>

            <div className="input-group">
              <label>Witnesses (if any)</label>
              <input
                type="text"
                name="witnesses"
                value={formData.witnesses}
                onChange={handleInputChange}
              />
            </div>
          </fieldset>

          <fieldset className="form-section">
            <legend>Supporting Documents</legend>
            <div className="input-group">
              <label>Upload Files</label>
              <input
                type="file"
                onChange={handleFileUpload}
                multiple
                accept="image/*,.pdf"
              />
              <small>Images or PDFs (max 5MB each)</small>
            </div>
          </fieldset>

          <div className="form-buttons">
            <button
              type="submit"
              className="submit-button"
              disabled={isLoading}
            >
              {isLoading ? 'Sending...' : 'Submit Report'}
            </button>
            <button
              type="button"
              className="cancel-button"
              onClick={() => navigate(-1)}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>

      <Footer />
    </div>
  );
};

export default AccidentReport;
