import React from 'react';
import { Link } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/Footer.css';
import '../CSS/Header.css';
import '../CSS/AboutPage.css';
import '../CSS/HomePage.css';

const AboutPage = () => {
  const isAuthenticated = localStorage.getItem('isAuthenticated');

  return (
    <div className="home-page">
            <Header isAuthenticated={isAuthenticated} />

      <main className="about-main">
        <div className="about-container">
          <section className="about-hero">
            <h1>Our Mission to Create Safer Spaces</h1>
            <p className="hero-subtitle">
              SpeakUpHer is dedicated to empowering individuals to report harassment safely and confidentially
            </p>
          </section>

          <section className="about-story">
            <div className="story-content">
              <h2>Our Story</h2>
              <p>
                Founded in 2025 in Jordan, SpeakUpHer emerged from a pressing need to address workplace and public harassment. 
                Our team of technologists, legal experts, and social activists came together to create a platform that makes 
                reporting harassment simple, secure, and effective.
              </p>
              <p>
                What started as a university project has grown into a movement, helping thousands find their voice 
                against harassment.
              </p>
            </div>
            <div className="story-image">
              <img src="/images/speakup.png" alt=" " />
            </div>
          </section>
        </div>
      </main>


    </div>
  );
};

export default AboutPage;
