import React from 'react';
// import { Link } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';

import '../CSS/Footer.css';
import '../CSS/Header.css';
import '../CSS/AboutPage.css';
import '../CSS/HomePage.css';

const AboutPage = () => {
  const isUserLoggedIn = localStorage.getItem('isAuthenticated');

  return (
    <div className="page-container">
      {/* Header component with login status */}
      <Header isAuthenticated={isUserLoggedIn} />

      {/* Main content of the About page */}
      <main className="about-content">
        
        <section className="hero-section">
          <h1>Our Mission to Create Safer Spaces</h1>
          <p>
            SpeakUpHer is dedicated to empowering individuals to report harassment safely and confidentially
          </p>
        </section>

        <section className="story-section">
          <div className="text-part">
            <h2>Our Story</h2>
            <p>
              Founded in 2025 in Jordan, SpeakUpHer emerged from a pressing need to 
              address workplace and public harassment. Our team of technologists, 
              legal experts, and social activists came together to create a platform 
              that makes reporting harassment simple, secure, and effective.
            </p>
            <p>
              What started as a university project has grown into a movement, 
              helping thousands find their voice against harassment.
            </p>
          </div>
          
          <div className="image-part">
            <img 
              src="/images/speakup.png" 
              alt="SpeakUpHer logo or related imagery" 
            />
          </div>
        </section>

      </main>

      <Footer />
    </div>
  );
};

export default AboutPage;
