import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../JS/Header';
import Footer from '../JS/Footer';
import '../CSS/AccidentReport.css';

const HarassmentReport = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    reporterName: '',
    incidentDate: '',
    incidentTime: '',
    location: '',
    submisionDate: '',
    incidentType: 'verbal',
    description: '',
    injuries: '',
    witnesses: '',
    files: null
  });
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleFileUpload = (e) => {
    setFormData({
      ...formData,
      files: e.target.files
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    if (!formData.reporterName || !formData.incidentDate || !formData.location || formData.submisionDate) {
      setError('Please fill in all required fields');
      setIsLoading(false);
      return;
    }

    try {
      await new Promise(resolve => setTimeout(resolve, 1500));
      alert('Harassment report submitted successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError('Submission failed. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="report-page">
      <Header isAuthenticated={localStorage.getItem('isAuthenticated')} />
      
      <div className="report-form-container">
        <h1 className="form-title">Harassment Report</h1>

        {error && <div className="error-message">{error}</div>}

        <form onSubmit={handleFormSubmit}>
          <fieldset className="form-section">
            <legend>Incident Details</legend>
            
            <div className="input-row">
              <div className="input-group">
                <label>Date *</label>
                <input
                  type="date"
                  name="incidentDate"
                  value={formData.incidentDate}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="input-group">
                <label>Time</label>
                <input
                  type="time"
                  name="incidentTime"
                  value={formData.incidentTime}
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="input-group">
              <label>Location *</label>
              <input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleInputChange}
                required
                placeholder="Where the incident occurred"
              />
            </div>

            <div className="input-group">
              <label>Type of Harassment *</label>
              <select
                name="incidentType"
                value={formData.incidentType}
                onChange={handleInputChange}
                required
              >
                <option value="verbal">Verbal Harassment</option>
                <option value="physical">Physical Harassment</option>
                <option value="sexual">Sexual Harassment</option>
                <option value="bullying">Bullying/Intimidation</option>
                <option value="cyber">Cyber Harassment</option>
                <option value="stalking">Stalking</option>
                <option value="other">Other</option>
              </select>
            </div>

            <div className="input-group">
              <label>Description *</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
                placeholder="Please describe what happened in detail..."
              />
            </div>

            <div className="input-group">
              <label>Witnesses (if any)</label>
              <input
                type="text"
                name="witnesses"
                value={formData.witnesses}
                onChange={handleInputChange}
                placeholder="Names of any witnesses"
              />
            </div>
          </fieldset>

          <fieldset className="form-section">
            <legend>Supporting Evidence</legend>
            <div className="input-group">
              <label>Upload Supporting Documents</label>
              <input
                type="file"
                onChange={handleFileUpload}
                multiple
                accept="image/*,.pdf,.doc,.docx"
              />
              <small>Screenshots, emails, messages, or documents (max 5MB each)</small>
            </div>
          </fieldset>

          <div className="form-buttons">
            <button
              type="submit"
              className="submit-button"
              disabled={isLoading}
            >
              {isLoading ? 'Submitting...' : 'Submit Report'}
            </button>
            <button
              type="button"
              className="cancel-button"
              onClick={() => navigate(-1)}
            >
              Cancel
            </button>
          </div>
        </form>
      </div>

      <Footer />
    </div>
  );
};

export default HarassmentReport;
